sourcesContent:
// Standard modules
import http from 'http';
import 'dotenv/config';
import 'regenerator-runtime';

// Modules from this project
import cluster from 'cluster';
import knex from 'knex';
import { LoggerUtil } from '../utils';
import App from '../app';

// Constants
import config from '../config/variables.config';
import { name } from '../../package.json';
import UrlsModel from '../models/urlsModel';

const { PORT } = config;

const init = async () => {
  App.init();
  const server = http.createServer(App.app);
  // console.log(555);
  const _onError = (error) => {
    if (error.syscall !== 'listen') {
      throw error;
    }
    const bind = typeof App.port === 'string' ? `Pipe ${App.port}` : `Port ${App.port}`;
    switch (error.code) {
    case 'EACCES':
      LoggerUtil.error(`${bind} requires elevated privileges`);
      process.exit(1);
      break;
    case 'EADDRINUSE':
      LoggerUtil.error(`${bind} is already in use`);
      process.exit(1);
      break;
    default:
      throw error;
    }
  };

  const _onListening = () => {
    const address = server.address();
    const bind = typeof address === 'string'
      ? `pipe ${address}`
      : `${address.port}`;

    LoggerUtil.info(`${name} started:`);
    LoggerUtil.info(`\tPort: ${bind}`);
    LoggerUtil.info(`\tEnvironment: ${App.env}`);
    LoggerUtil.info(`\tNode version: ${process.version}`);
    LoggerUtil.info(`\tStart date: ${(new Date()).toUTCString()} \n`);
  };
  console.log(66);
  server.listen(PORT);
  server.on('error', _onError);
  server.on('listening', _onListening);
};
if (cluster.isMaster) {
  for (let i = 0; i < 8; i++) {
    cluster.fork();
  }
  cluster.on('online', (worker) => {
    console.log(`Worker ${worker.process.pid} is online.`);
  });
  cluster.on('exit', (worker) => {
    console.log(`worker ${worker.process.pid} died.`);
    // cluster.fork();
  });
} else {
  const server = http.createServer(App.app);
  server.listen(PORT);

  UrlsModel.getUrls();
}

export default init().catch(LoggerUtil.error);

