sourcesContent:
// NPM Modules

import { Model } from 'objection';

import PSQLStorage from '../storage/psql.storage';

import axios from "axios";

// const knex = PSQLStorage.init();

class UrlsModel extends Model {
  static get idColumn() { return 'id'; }

  static get tableName() { return 'domain'; }

  static get jsonSchema() {
    return {
      type: 'object',
      required: ['id', 'domain'],
      properties: {
        id: { type: 'integer' },
        domain: { type: 'string', minLength: 1, maxLength: 255 },
        Status: { type: 'string', enum: ['Active', 'Passive'] }
      }
    };
  }

  // // Methods

  static async getUrls() {
    const {knex} = PSQLStorage
    const urls = await knex.from('links').select('domain').offset(1000).limit(10);

    const fUrls = urls.map((ur) => `https://${ur.domain}`);

    console.log(5555, fUrls);
    // function pr(payload) {
    //   // const { knex } = PSQLStorage;
    //   return Promise.allSettled(payload.map((url) => {
    //     .fetch(url, {
    //       method: 'HEAD',
    //       mode: 'no-cors'
    //     })
    //       .then(() => {
    //         console.log('Available =>', url);
    //       }, () => {
    //         console.log('InValid =>', url);
    //       });
    //   }));
    // }
    // // console.log(222222222222222222222222222222222222222222222,fUrls);
    //  pr(fUrls);

    Promise.allSettled(fUrls.map((url) => {
      axios(url, {
        method: 'HEAD',
        mode: 'no-cors'
      })
        .then(() => {
          console.log('Available =>', url);
        }, () => {
          console.log('InValid =>', url);
        });
    }));
  }
  // this.getUrls(1000,100)
}
//


export default UrlsModel;

