sourcesContent:
// NPM Modules

import PSQLStorage from '../storage/psql.storage';

import status from '../enum/status.enum'

class UrlsModel {
  // // Methods
 static async getUrls() {
    const {knex} = PSQLStorage;
    const urls =  await knex
    .from('links')
    .select('domain', 'id')
    .offset(1000000)
    .limit(5)
    .then((rows) => {
      return rows;
    });
    return urls;
  }

  static async setPassive(id) {
    const {knex} = PSQLStorage;
    return knex
    .from('links')
    .whereIn('id', id)
    .update({status: ''})
    .then(() => {
        console.log('Table update');
      }
    )
  }
}
export default UrlsModel;

