import { UrlsModel } from '../models';
import fetch from 'node-fetch';
import PSQLStorage from '../storage/psql.storage';


let invalidUrls = [];

let countUrls = [];

class UrlService {
    static async getInvalidUrls(urls) {
        const controller = new AbortController();
        Promise.allSettled(urls.map((item) => {
            let url = this.buildUrlFromDomains(item.domain);  
            setTimeout(() => 
            fetch(url, {
              method: 'GET',
              mode: 'no-cors',
            })
            .then(() => {
              console.log('Available =>', url);
              countUrls.push(url.length)
              console.log(countUrls.length, ' Length Updated');
            })
            .catch((e) => {
              invalidUrls.push(item.id);                                
              console.log('InValid =>', url)
              countUrls.push(url.length)
              console.log(countUrls.length, ' Length Updated');
                  console.log('Table update');
        }),5000)}
        ))
    } 
    static async checkUrls() {
        const items = await UrlsModel.getUrls();
        const invalidIds = await this.getInvalidUrls(items);
        return invalidIds;
    }
    static buildUrlFromDomains(domain) {
      return `https://${domain}`
    }
    static async CountLength(arr){
      console.log(arr.length);
    }
}
console.log(countUrls.length);

export default UrlService;

