sourcesContent:
import fetch from 'node-fetch';
import { map } from 'lodash';
import { UrlsModel } from '../models';
import PSQLStorage from '../storage/psql.storage';

import status from '../enum/status.enum';

// const invalidUrls = [];

// const countUrls = [];

const array = [];

class UrlService {
  static async checkUrls(offset, limit) {
    const items = await UrlsModel.getUrls(offset, limit);
    const invalidIds = await this.getInvalidUrls(items);
    return invalidIds;
  }

  static buildUrlFromDomains(domain) {
    return `https://${domain}`;
  }

  static async CountLength(arr) {
    console.log(arr.length);
  }

  static async getInvalidUrls(urls) {
    try {
      const result = await Promise.allSettled(urls.map((item) => {
        const url = this.buildUrlFromDomains(item.domain);
        return fetch(url, {
          method: 'GET',
          mode: 'no-cors'
        });
      }));

      for (let i = 0; i < result.length; i += 1) {
        console.log(result[i].status, 44444);
        if (result[i].status === 'fulfilled') {
          console.log(i);
        } else {
          console.log('amen inch vat e ==>>', i);
          array.push(i);
          console.log(array);
        }
      }

      if (array.length > 0) {
        const { knex } = PSQLStorage;
        knex
          .from('links')
          .whereIn('id', array)
          .update({ status: 'pasive' })
          .then(() => {
            console.log('Table updated');
          });
      }
    } catch (e) {
      console.log(e);
    }
  }
}

export default UrlService;

