sourcesContent:
// NPM Modules

import PSQLStorage from '../storage/psql.storage';

class UrlsModel {
  // // Methods
  static async getUrls(offset, limit) {
    const { knex } = PSQLStorage;
    const urls = await knex
      .from('links')
      .select('domain', 'id')
      // .orderBy('id')
      .offset(offset)
      .limit(limit)
      .then((rows) => rows);
      // console.log(urls);
    return urls;
  }

  static async setPassive(array) {
    const { knex } = PSQLStorage;
    return knex
      .from('links')
      .whereIn('id', array)
      .update({ status: 'passive' })
      .then(() => {
        console.log('Table update');
      });
  }
}
export default UrlsModel;

