    USER = _PSQL$PSQL.USER,
    PASSWORD = _PSQL$PSQL.PASSWORD;
var _default = {
  development: {
    client: 'pg',
    useNullAsDefault: true,
    seeds: {
      directory: 'seeds/dev'
    },
    connection: {
      port: PORT,
      host: HOST,
      database: DATABASE,
      user: USER,
      password: PASSWORD
    }
  },
  option: {
    client: 'pg',
    useNullAsDefault: true,
    connection: {
      port: PORT,
      host: HOST,
      database: DATABASE,
      user: USER,
      password: PASSWORD
    }
